import logo from './logo.svg';
import {useState} from 'react';
import './App.css';
import axios from 'axios'

function App() {

  const [name,setName]=useState("");
  const [myImg,setmyImg]=useState("");
  const [response,setResponse]=useState("");
  const [errMsg,setErrMsg]=useState("");


  const [user,setUser]=useState({name:'',imagePath:'',userId:''})

  const handleSubmit = async (e) =>
  {
    e.preventDefault();
    const formData= new FormData();
    formData.append('name',name);
    formData.append('myImg',myImg);
    await fetch("http://localhost:5000/uploadForm",
    {
      method:'POST',
      body:formData
    })
    .then(res=>res.json())
    .then(res=>{
      setResponse(res.message)
      console.log(res.data.image)
      setUser(
        {
          name:res.data.name,
          imagePath:res.data.image,
          userId:res.data.id

        })
      })
    .catch(err=>setErrMsg(err.message))

  }




  return (
    <div>
     <form onSubmit={handleSubmit} encType="multipart/form-data">
       <input type="file" accept=".png,.jpeg" name="myImg" onChange={e=>setmyImg(e.target.files[0])}></input>
       <input type="text" placeholder="Enter your name" onChange={e=>setName(e.target.value)}></input>
       <button type="submit">Submit</button>
     </form>
     {response!=''?
     <div>
     <h1>{response}</h1>
     <h2>{user.name}</h2>
     <img src={`http://localhost:5000/${user.imagePath}`}/>
     </div>
     :<h1>{errMsg}</h1>}

    </div>
  );
}

export default App;
